SELECT \n    SUBSTRING(LTRIM(STR(Blockid)), 1, 6) AS blockid,\n    COUNT(DISTINCT RIGHT(Blockid, 2)) AS noofBlocks,\n    SUM(CASE WHEN Stateorprivate = 'Deemana' THEN 1 ELSE 0 END) AS Deemana,\n    SUM(CASE WHEN Stateorprivate = 'State' THEN 1 ELSE 0 END) AS State,\n    SUM(CASE WHEN Stateorprivate = 'Private' THEN 1 ELSE 0 END) AS Private,\n    COUNT(*) AS Amt_of_Deemana_State_Private\nFROM \n    LandParcels\nGROUP BY \n    SUBSTRING(LTRIM(STR(Blockid)), 1, 6)\nORDER BY \n    blockid ASC;