SELECT p.*\nFROM Property p\nWHERE EXISTS (\n  SELECT 1\n  FROM Property_Feature pf\n  JOIN Feature f ON pf.fid = f.fid\n  WHERE pf.pid = p.pid AND f.name = 'Air Conditioning'\n)\nAND EXISTS (\n  SELECT 1\n  FROM Property_Feature pf\n  JOIN Feature f ON pf.fid = f.fid\n  WHERE pf.pid = p.pid AND f.name = 'Pool'\n)\nAND EXISTS (\n  SELECT 1\n  FROM Property_Feature pf\n  JOIN Feature f ON pf.fid = f.fid\n  WHERE pf.pid = p.pid AND f.name = 'Laundry On-Site'\n);