CREATE TABLE entities (\n  id INT PRIMARY KEY,\n  name VARCHAR(255)\n);\n\nCREATE TABLE attributes (\n  id INT PRIMARY KEY,\n  name VARCHAR(255),\n  data_type VARCHAR(255)\n);\n\nCREATE TABLE values (\n  id INT PRIMARY KEY,\n  entity_id INT,\n  attribute_id INT,\n  value VARCHAR(255),\n  FOREIGN KEY (entity_id) REFERENCES entities(id),\n  FOREIGN KEY (attribute_id) REFERENCES attributes(id)\n);