CREATE TABLE GradStudent (\n  id INT PRIMARY KEY,\n  firstName VARCHAR(50),\n  lastName VARCHAR(50),\n  birthDate DATE\n);\n\nCREATE TABLE TeachAsst (\n  id INT PRIMARY KEY,\n  student_id INT,\n  courseAssignment VARCHAR(100),\n  FOREIGN KEY (student_id) REFERENCES GradStudent(id)\n);\n\nCREATE TABLE ResearchAsst (\n  id INT PRIMARY KEY,\n  student_id INT,\n  researchGrant VARCHAR(100),\n  FOREIGN KEY (student_id) REFERENCES GradStudent(id)\n);