DECLARE @fromCategoryID INT = 1;\nDECLARE @toCategoryID INT = 2;\n\nINSERT INTO Questions (Question, CategoryID)\nSELECT q.Question, @toCategoryID\nFROM Questions q\nWHERE q.CategoryID = @fromCategoryID;\n\nINSERT INTO Selections (Selection, QuestionID)\nSELECT s.Selection, q2.QuestionID\nFROM Selections s\nINNER JOIN Questions q1 ON s.QuestionID = q1.QuestionID\nINNER JOIN Questions q2 ON q1.Question = q2.Question AND q2.CategoryID = @toCategoryID;