-- Create a table with an encrypted column\nCREATE TABLE sensitive_data (\n  id INT PRIMARY KEY,\n  encrypted_data VARBINARY(255)\n);\n\n-- Insert encrypted data into the table\nINSERT INTO sensitive_data (id, encrypted_data)\nVALUES (1, ENCRYPT('sensitive_data', 'secret_key'));\n\n-- Retrieve and decrypt the data\nSELECT CAST(DECRYPT(encrypted_data, 'secret_key') AS CHAR) AS decrypted_data\nFROM sensitive_data WHERE id = 1;