-- Create two tables with 1 million rows each\nCREATE TABLE table_1 (id INT, name VARCHAR(50));\nCREATE TABLE table_2 (id INT, email VARCHAR(100));\n\n-- Insert data\nINSERT INTO table_1 (id, name) SELECT i, 'Name ' || i FROM generate_series(1, 1000000) i;\nINSERT INTO table_2 (id, email) SELECT i, 'email_' || i || '@example.com' FROM generate_series(1, 1000000) i;\n\n-- Query with aliases\nEXPLAIN (ANALYZE) SELECT T1.name, T2.email\nFROM table_1 T1\nJOIN table_2 T2 ON T1.id = T2.id\nWHERE T1.id < 10000;\n\n-- Query without aliases\nEXPLAIN (ANALYZE) SELECT table_1.name, table_2.email\nFROM table_1\nJOIN table_2 ON table_1.id = table_2.id\nWHERE table_1.id < 10000;