-- Script data for a single table\nDECLARE @tableName nvarchar(100) = 'YourTableName';\nDECLARE @sql nvarchar(max) = '';\n\nSELECT @sql += 'INSERT INTO ' + QUOTENAME(@tableName) + ' (' + STUFF((SELECT ', ' + QUOTENAME(column_name) \nFROM sys.columns c\nJOIN sys.tables tb ON c.object_id = tb.object_id\nWHERE tb.name = @tableName\nFOR XML PATH('')), 1, 1, '') + ') \nVALUES ('\n+ STUFF((SELECT ', ''' + CONVERT(nvarchar(max), column_value) + ''''\nFROM (\n  SELECT name AS column_name, \n         t.value AS column_value\n  FROM sys.columns c\n  JOIN sys.tables tb ON c.object_id = tb.object_id\n  CROSS APPLY (SELECT TOP 1 * FROM YourTableName FOR XML PATH('')) AS t(value)\n  WHERE tb.name = @tableName\n) AS t\nFOR XML PATH('')), 1, 1, '') + ');';\n\nPRINT @sql;\n\n-- Script data for all tables\nDECLARE @sqlAll nvarchar(max) = '';\nDECLARE cur CURSOR FOR SELECT name FROM sys.tables;\n\nOPEN cur;\n\nFETCH NEXT FROM cur INTO @tableName;\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n  SELECT @sql += 'INSERT INTO ' + QUOTENAME(@tableName) + ' (' + STUFF((SELECT ', ' + QUOTENAME(column_name) \n  FROM sys.columns c\n  JOIN sys.tables tb ON c.object_id = tb.object_id\n  WHERE tb.name = @tableName\n  FOR XML PATH('')), 1, 1, '') + ') \nVALUES ('\n  + STUFF((SELECT ', ''' + CONVERT(nvarchar(max), column_value) + ''''\n  FROM (\n    SELECT name AS column_name, \n           t.value AS column_value\n    FROM sys.columns c\n    JOIN sys.tables tb ON c.object_id = tb.object_id\n    CROSS APPLY (SELECT TOP 1 * FROM YourTableName FOR XML PATH('')) AS t(value)\n    WHERE tb.name = @tableName\n  ) AS t\n  FOR XML PATH('')), 1, 1, '') + ');';\n  \n  FETCH NEXT FROM cur INTO @tableName;\nEND\n\nCLOSE cur;\nDEALLOCATE cur;\n\nPRINT @sqlAll;