SELECT \n    'UPDATE ' + OBJECT_NAME(fk.parent_object_id) + \n    ' SET ' + COL_NAME(fk.parent_object_id, fk.parent_column_id) + \n    ' = t.SequentialGuid' + \n    ' FROM ' + OBJECT_NAME(fk.parent_object_id) + \n    ' INNER JOIN YourTable t ON ' + COL_NAME(fk.parent_object_id, fk.parent_column_id) + ' = t.OldGuid'\nFROM \n    sys.foreign_keys AS fk\nWHERE \n    fk.referenced_object_id = OBJECT_ID('YourTable');