-- Create a new user for replication\nCREATE USER 'replication_user'@'%' IDENTIFIED BY 'replication_password';\nGRANT REPLICATION SLAVE ON *.* TO 'replication_user'@'%';\n\n-- Configure bi-directional replication\nCHANGE MASTER TO MASTER_HOST='server_a', MASTER_PORT=3306, MASTER_USER='replication_user', MASTER_PASSWORD='replication_password';\nSTART SLAVE;\n\n-- Create a table with a unique identifier and timestamp column\nCREATE TABLE names (\n  id INT PRIMARY KEY,\n  name VARCHAR(255),\n  uuid VARCHAR(36) UNIQUE,\n  updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP\n);\n\n-- Insert data into the table\nINSERT INTO names (id, name, uuid) VALUES (1, 'Tom', 'uuid_1');\nINSERT INTO names (id, name, uuid) VALUES (2, 'Scott', 'uuid_2');