DECLARE @sql nvarchar(max) = '';\nDECLARE @week nvarchar(10);\n\nDECLARE week_cursor CURSOR FOR\n    SELECT \n        name \n    FROM \n        sys.columns \n    WHERE \n        object_id = OBJECT_ID('YourTable') \n        AND name LIKE 'Week%';\n\nOPEN week_cursor;\n\nFETCH NEXT FROM week_cursor INTO @week;\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    SET @sql = @sql + '[' + @week + '],';\n    FETCH NEXT FROM week_cursor INTO @week;\nEND\n\nCLOSE week_cursor;\nDEALLOCATE week_cursor;\n\nSET @sql = 'SELECT Year, Week, Sales FROM (SELECT Year, ' + LEFT(@sql, LEN(@sql) - 1) + ' FROM YourTable) p UNPIVOT (Sales FOR Week IN (' + LEFT(@sql, LEN(@sql) - 1) + ')) AS unpiv;';\n\nEXEC sp_executesql @sql;