-- Create a table to store the XML data\nCREATE TABLE XmlData (\n    Id INT IDENTITY PRIMARY KEY,\n    XmlContent XML\n);\n\n-- Create a stored procedure to import the XML data\nCREATE PROCEDURE sp_ImportXmlData\n    @XmlFile NVARCHAR(MAX)\nAS\nBEGIN\n    DECLARE @Xml XML;\n    SET @Xml = CAST(@XmlFile AS XML);\n\n    -- Use OPENXML to read the XML data\n    DECLARE @idoc INT;\n    EXEC sp_xml_preparedocument @idoc OUTPUT, @Xml;\n\n    -- Insert the XML data into the table\n    INSERT INTO XmlData (XmlContent)\n    SELECT * FROM OPENXML(@idoc, '/*');\n\n    -- Clean up\n    EXEC sp_xml_removedocument @idoc;\nEND;\n\n-- Call the stored procedure to import the XML data\nEXEC sp_ImportXmlData 'C:\Path\To\Your\XmlFile.xml';