CREATE TABLE People (\n    PersonId INT IDENTITY(1,1) PRIMARY KEY,\n    Name VARCHAR(100),\n    SSN VARCHAR(20),\n    -- ... other columns\n);\n\nCREATE TABLE Loans (\n    LoanId INT IDENTITY(1,1) PRIMARY KEY,\n    Amount DECIMAL(18, 2),\n    -- ... other columns\n);\n\nCREATE TABLE BorrowerGroups (\n    GroupId INT IDENTITY(1,1) PRIMARY KEY\n);\n\nCREATE TABLE Borrowers (\n    BorrowerId INT IDENTITY(1,1) PRIMARY KEY,\n    GroupId INT,\n    PersonId INT,\n    FOREIGN KEY (GroupId) REFERENCES BorrowerGroups(GroupId),\n    FOREIGN KEY (PersonId) REFERENCES People(PersonId)\n);\n\nCREATE TABLE LoanBorrowerGroups (\n    LoanId INT,\n    GroupId INT,\n    FOREIGN KEY (LoanId) REFERENCES Loans(LoanId),\n    FOREIGN KEY (GroupId) REFERENCES BorrowerGroups(GroupId)\n);