SELECT \n    t.name AS TableName,\n    t.value AS TableDescription,\n    c.name AS ColumnName,\n    ty.name AS DataType\nFROM \n    sys.tables AS t\nINNER JOIN \n    sys.columns AS c ON t.object_id = c.object_id\nINNER JOIN \n    sys.types AS ty ON c.system_type_id = ty.system_type_id\nLEFT JOIN \n    sys.extended_properties AS p ON t.object_id = p.major_id AND p.minor_id = 0\nWHERE \n    p.name = 'MS_Description'\nORDER BY \n    t.name, c.column_id;