-- Create a stored procedure to delete records based on a date/time interval\nCREATE PROCEDURE DeleteExpiredRecords\nAS\nBEGIN\n    DELETE FROM YourTable\n    WHERE YourTimeStampColumn < DATEADD(day, -30, GETDATE()); -- adjust the interval as needed\nEND\nGO\n\n-- Create a SQL Server Agent job to run the stored procedure\n-- (this requires SQL Server Agent to be running and configured)\n\n-- Step 1: Create a new job\nEXEC sp_add_job @job_name = 'Delete Expired Records';\n\n-- Step 2: Add a step to the job to run the stored procedure\nEXEC sp_add_jobstep \n    @job_name = 'Delete Expired Records', \n    @step_name = 'Delete Expired Records', \n    @subsystem = 'TSQL', \n    @command = 'EXEC DeleteExpiredRecords', \n    @retry_attempts = 0, \n    @retry_interval = 0;\n\n-- Step 3: Add a schedule to the job to run daily\nEXEC sp_add_jobschedule \n    @job_name = 'Delete Expired Records', \n    @name = 'Daily', \n    @freq_type = 4, -- daily\n    @freq_interval = 1, \n    @freq_recurrence_factor = 1, \n    @active_start_time = '02:00:00', -- adjust the start time as needed\n    @active_end_time = '02:00:00';