ALTER FUNCTION [dbo].[FullTextSearch] \n(\n    @Columns nvarchar(100), \n    @SearchPhrase nvarchar(100)\n)\nRETURNS TABLE \nAS    \nRETURN \n(\n    SELECT * \n    FROM projects as P \n    INNER JOIN containstable(PROJECTS, Title, @SearchPhrase) as K \n    ON P.project_id = K.[KEY]\n    WHERE @Columns = 'Title'\n\n    UNION ALL\n\n    SELECT * \n    FROM projects as P \n    INNER JOIN containstable(PROJECTS, Project_Details, @SearchPhrase) as K \n    ON P.project_id = K.[KEY]\n    WHERE @Columns = 'Project_Details'\n\n    UNION ALL\n\n    SELECT * \n    FROM projects as P \n    INNER JOIN containstable(PROJECTS, Contact_Info, @SearchPhrase) as K \n    ON P.project_id = K.[KEY]\n    WHERE @Columns = 'Contact_Info'\n\n    UNION ALL\n\n    SELECT * \n    FROM projects as P \n    INNER JOIN containstable(PROJECTS, Project_Description, @SearchPhrase) as K \n    ON P.project_id = K.[KEY]\n    WHERE @Columns = 'Project_Description'\n\n    UNION ALL\n\n    SELECT * \n    FROM projects as P \n    INNER JOIN containstable(PROJECTS, (Title, Project_Details, Contact_Info, Project_Description), @SearchPhrase) as K \n    ON P.project_id = K.[KEY]\n    WHERE @Columns = 'All' OR @Columns NOT IN ('Title', 'Project_Details', 'Contact_Info', 'Project_Description')\n)