USE msdb\nGO\n\nEXEC sp_add_alert @name=N'SQL Server Agent Stopped', \n                  @message_id=0, \n                  @severity=0, \n                  @enabled=1, \n                  @delay_between_responses=0, \n                  @include_event_description_in=1, \n                  @notification_message=N'SQL Server Agent stopped on %s';\n\nEXEC sp_add_notification @alert_name=N'SQL Server Agent Stopped', \n                         @operator_name=N'YourOperatorName', \n                         @notification_method=1;