-- Create a temp table with a check constraint\nCREATE TABLE #TempTable (\n    Id INT,\n    Value INT CHECK (Value > 0)\n);\n\n-- Verify the constraint exists\nSELECT * FROM tempdb.sys.check_constraints\nWHERE parent_object_id = OBJECT_ID('tempdb..#TempTable');\n\n-- Drop the temp table\nDROP TABLE #TempTable;\n\n-- Verify the constraint no longer exists\nSELECT * FROM tempdb.sys.check_constraints\nWHERE parent_object_id = OBJECT_ID('tempdb..#TempTable');