DECLARE @COUNTER INT\nDECLARE @TOTAL INT\nDECLARE @TABLE CHAR(128)\nDECLARE @COLUMN CHAR(128)\nDECLARE @COLUMNTYPE CHAR(128)\nDECLARE @COLUMNVALUE INT\nDECLARE @SQL NVARCHAR(4000)\n\n-- What we are looking for\nSET @COLUMNTYPE = 'INT'\nSET @COLUMNVALUE = 3\nSET @COUNTER = 0\n\n-- Find out how many possible columns exist\nSELECT @TOTAL = COUNT(*)  \nFROM INFORMATION_SCHEMA.Columns as Columns \nJOIN INFORMATION_SCHEMA.TABLES as tables \nOn Columns.TABLE_NAME = tables.TABLE_NAME\nWHERE Columns.DATA_TYPE = @COLUMNTYPE\nPRINT CAST(@TOTAL AS CHAR) + ' possible columns'\n\nWHILE @COUNTER < @TOTAL\nBEGIN\n    SET @COUNTER = @COUNTER +1\n    \n    -- Get the current table and column name\n    SELECT TOP 1 @TABLE = TABLE_NAME, @COLUMN = COLUMN_NAME\n    FROM INFORMATION_SCHEMA.Columns as Columns \n    JOIN INFORMATION_SCHEMA.TABLES as tables \n    On Columns.TABLE_NAME = tables.TABLE_NAME\n    WHERE Columns.DATA_TYPE = @COLUMNTYPE\n    AND Columns.ORDINAL_POSITION = @COUNTER\n    \n    -- Build the SQL query to search for the value\n    SET @SQL = 'SELECT ''' + @TABLE + '.' + @COLUMN + ''' AS COLUMN_NAME, * FROM ' + @TABLE + ' WHERE ' + @COLUMN + ' = ' + CONVERT(VARCHAR, @COLUMNVALUE)\n    \n    -- Execute the SQL query\n    EXEC sp_executesql @SQL\n    \nEND