-- Insert a new order\nINSERT INTO Ordre (CPR, Dato, SamletPris)\nVALUES (12345678, '2023-02-20 14:30:00', 0.0);\n\n-- Get the ID of the newly inserted order\nSELECT last_insert_rowid() AS Onr;\n\n-- Insert products into the order\nINSERT INTO VareOrdre (Onr, Vnr, Antal)\nVALUES\n    (1, 1, 2),  -- 2 products with Vnr = 1\n    (1, 2, 1);  -- 1 product with Vnr = 2\n\n-- Update the total price of the order\nUPDATE Ordre\nSET SamletPris = (SELECT SUM(Vare.Pris * VareOrdre.Antal)\n                  FROM VareOrdre\n                  JOIN Vare ON Vare.Vnr = VareOrdre.Vnr\n                  WHERE VareOrdre.Onr = 1)\nWHERE Onr = 1;