-- Example SQL script to archive processed records\nCREATE TABLE archive_transactions (\n    id INT,\n    transaction_date DATE,\n    -- other columns...\n);\n\nINSERT INTO archive_transactions\nSELECT * FROM main_transactions\nWHERE processed = TRUE AND transaction_date < DATE_SUB(CURDATE(), INTERVAL 60 DAY);\n\nDELETE FROM main_transactions\nWHERE processed = TRUE AND transaction_date < DATE_SUB(CURDATE(), INTERVAL 60 DAY);