CREATE TABLE LookupTable (\n  ID INT PRIMARY KEY,\n  Name VARCHAR(50)\n);\n\nCREATE TABLE OriginalTable (\n  ID INT PRIMARY KEY,\n  LookupID INT,\n  Value VARCHAR(50),\n  FOREIGN KEY (LookupID) REFERENCES LookupTable(ID)\n);\n\n-- Sample data:\nINSERT INTO LookupTable (ID, Name) VALUES (1, 'Foo'), (2, 'Bar');\n\nINSERT INTO OriginalTable (ID, LookupID, Value)\nVALUES (1, 1, 'some value 1'), (2, 1, 'some value 2'), (3, 1, 'some value 3'), (4, 2, 'some value 3');