CREATE TABLE tblusers (\n  UsrID INT PRIMARY KEY,\n  First VARCHAR(50),\n  Last VARCHAR(50),\n  DOB DATE,\n  Phone_Number VARCHAR(20),\n  -- ... other biographical information ...\n);\n\nCREATE TABLE tbluser_pictures (\n  PictureID INT PRIMARY KEY,\n  UsrID INT,\n  PictureName VARCHAR(255),\n  PicturePath VARCHAR(255),\n  IsDefault TINYINT(1) DEFAULT 0,\n  CreatedAt TIMESTAMP DEFAULT CURRENT_TIMESTAMP,\n  FOREIGN KEY (UsrID) REFERENCES tblusers(UsrID)\n);\n\nCREATE TABLE tbluser_thumbnails (\n  ThumbnailID INT PRIMARY KEY,\n  PictureID INT,\n  ThumbnailPath VARCHAR(255),\n  CreatedAt TIMESTAMP DEFAULT CURRENT_TIMESTAMP,\n  FOREIGN KEY (PictureID) REFERENCES tbluser_pictures(PictureID)\n);