CREATE PARTITION FUNCTION pf_huge_table (INT)\nAS RANGE RIGHT FOR VALUES (1000000, 2000000, 3000000);\n\nCREATE PARTITION SCHEME ps_huge_table\nAS PARTITION pf_huge_table\nALL TO ('PRIMARY');\n\nCREATE TABLE huge_table_partitioned (\n    id INT,\n    -- other columns...\n) ON ps_huge_table (id);\n\n-- Update each partition separately\nALTER TABLE huge_table_partitioned\nADD new_column INT DEFAULT 0 WITH (ONLINE = ON);