SELECT \n  COUNT(j.jobseeker_id) AS new_registrations,\n  COUNT(c.cv_id) AS uploaded_cvs\nFROM \n  tb_jobseeker j\n  JOIN tb_industry i ON j.industry_id = i.industry_id\n  LEFT JOIN tb_cv c ON j.jobseeker_id = c.jobseeker_id\nWHERE \n  j.created_at >= '2009-05-01 00:00:00'\n  AND j.created_at <= '2009-05-31 23:59:59'\n  AND c.created_at >= '2009-05-01 00:00:00'\n  AND c.created_at <= '2009-05-31 23:59:59'\nGROUP BY \n  i.description, MONTH(j.created_at)