SELECT \n    i.name AS IndexName,\n    ius.user_seeks AS UserSeeks,\n    ius.user_scans AS UserScans,\n    ius.user_lookups AS UserLookups,\n    ius.user_updates AS UserUpdates\nFROM \n    sys.dm_db_index_usage_stats ius\nINNER JOIN \n    sys.indexes i ON ius.object_id = i.object_id AND ius.index_id = i.index_id\nWHERE \n    ius.database_id = DB_ID('your_database_name')\nORDER BY \n    UserSeeks DESC;