CREATE TYPE ArrayTableType AS TABLE (FieldName nvarchar(50), Value nvarchar(50));\nGO\n\nCREATE PROCEDURE InsertArray\n    @Values ArrayTableType READONLY\nAS\nBEGIN\n    INSERT INTO YourTable (Field1, Field2, Field3)\n    SELECT [Field1], [Field2], [Field3]\n    FROM \n        (SELECT FieldName, Value\n         FROM @Values) AS SourceTable\n    UNPIVOT \n        (Value FOR FieldName IN ([Field1], [Field2], [Field3])) AS UnpivotTable;\nEND\nGO\n\nDECLARE @Values ArrayTableType\n\nINSERT INTO @Values (FieldName, Value)\nVALUES \n    ('Field1', 'Value1'),\n    ('Field2', 'Value2'),\n    ('Field3', 'Value3')\n\nEXEC InsertArray @Values