-- Detach the database\nEXEC sp_detach_db 'YourDatabaseName', 'true';\n\n-- Move the database files and the full-text catalog directory to the new server.\n\n-- Attach the database on the new server\nCREATE DATABASE [YourDatabaseName] ON \n    (FILENAME = 'C:\Data\YourDatabaseName.mdf'),\n    (FILENAME = 'C:\Data\YourDatabaseName_log.ldf'),\n    -- Include paths for any additional files\n    FOR ATTACH;