CREATE TABLE folder\n(\n    ID INT IDENTITY PRIMARY KEY,\n    FolderName NVARCHAR(50) NULL,  -- Allow NULL for dynamic folders\n    DynamicFieldID INT NULL,  -- Allow NULL for static folders\n    ParentID INT FOREIGN KEY REFERENCES folder,\n    CONSTRAINT CK_Folder_Type CHECK ((FolderName IS NULL AND DynamicFieldID IS NOT NULL) OR (FolderName IS NOT NULL AND DynamicFieldID IS NULL))\n)