-- Retrieve the wrapped symmetric key from the local database\nDECLARE @WrappedKey VARBINARY(MAX);\nSELECT @WrappedKey = WrappedKey FROM MyTable;\n\n-- Unwrap the symmetric key using the private key\nDECLARE @UnwrappedKey VARBINARY(MAX);\nSET @UnwrappedKey = DECRYPTBYASYMKEY(MyAsymmetricKey, @WrappedKey);\n\n-- Decrypt the data using the unwrapped symmetric key\nDECLARE @DecryptedData VARBINARY(MAX);\nSET @DecryptedData = DECRYPTBYKEY(@UnwrappedKey, 'data_to_decrypt');