-- Create a temporary table to hold the chosen id for each name\nCREATE TEMPORARY TABLE chosen_ids AS\nSELECT name, MIN(id) AS chosen_id\nFROM your_table\nGROUP BY name;\n\n-- Update the other tables to reference the chosen id\nUPDATE other_table1\nJOIN chosen_ids ON other_table1.name = chosen_ids.name\nSET other_table1.id = chosen_ids.chosen_id;\n\n-- Repeat the above UPDATE statement for the other tables\n\n-- Remove the duplicate entries from your original table\nDELETE yt1\nFROM your_table yt1\nJOIN your_table yt2 ON yt1.name = yt2.name AND yt1.id > yt2.id;