SELECT \n    'CREATE TABLE ' + QUOTENAME(s.name) + '.' + QUOTENAME(t.name) + ' (' + \n    STRING_AGG(CAST(c.name AS nvarchar(max)) + ' ' + c.system_type_name, ', ') + ');'\nFROM \n    sys.tables t \nINNER JOIN \n    sys.schemas s ON t.schema_id = s.schema_id\nINNER JOIN \n    sys.columns c ON t.object_id = c.object_id\nWHERE \n    t.name NOT LIKE 'aspnet_%'\nORDER BY \n    t.name;