CREATE TABLE #Values (\n    FieldName nvarchar(50),\n    Value nvarchar(50)\n)\n\nINSERT INTO #Values (FieldName, Value)\nVALUES \n    ('Field1', 'Value1'),\n    ('Field2', 'Value2'),\n    ('Field3', 'Value3')\n\nINSERT INTO YourTable (Field1, Field2, Field3)\nSELECT [Field1], [Field2], [Field3]\nFROM \n    (SELECT FieldName, Value\n     FROM #Values) AS SourceTable\nUNPIVOT \n    (Value FOR FieldName IN ([Field1], [Field2], [Field3])) AS UnpivotTable;\n\nDROP TABLE #Values