SELECT \n    a.NAME_LAST, \n    a.NAME_FIRST, \n    ca.status, \n    AVG(r.score) AS average_score, \n    COUNT(r.review_id) AS review_count\nFROM \n    application a\nINNER JOIN \n    committee_applications ca ON a.application_id = ca.application_id\nLEFT JOIN \n    reviews r ON a.application_id = r.application_id\nGROUP BY \n    a.application_id, a.NAME_LAST, a.NAME_FIRST, ca.status