CREATE TABLE Subjects (\n  SubjectID INT PRIMARY KEY,\n  Name VARCHAR(50) NOT NULL\n);\n\nCREATE TABLE Students (\n  StudentID INT PRIMARY KEY,\n  Name VARCHAR(100) NOT NULL\n);\n\nCREATE TABLE Grades (\n  GradeID INT PRIMARY KEY,\n  StudentID INT NOT NULL,\n  SubjectID INT NOT NULL,\n  Trimester TINYINT NOT NULL,  -- 1, 2, or 3\n  Average DECIMAL(3, 2) NOT NULL,\n  MissedClasses INT NOT NULL,\n  RecoveringGrade DECIMAL(3, 2) NOT NULL,\n  FOREIGN KEY (StudentID) REFERENCES Students(StudentID),\n  FOREIGN KEY (SubjectID) REFERENCES Subjects(SubjectID)\n);\n\nCREATE TABLE YearlyGrades (\n  YearlyGradeID INT PRIMARY KEY,\n  StudentID INT NOT NULL,\n  SubjectID INT NOT NULL,\n  YearAverage DECIMAL(3, 2) NOT NULL,\n  FinalRecoveringGrade DECIMAL(3, 2) NOT NULL,\n  FOREIGN KEY (StudentID) REFERENCES Students(StudentID),\n  FOREIGN KEY (SubjectID) REFERENCES Subjects(SubjectID)\n);