-- Instead of a cursor\nDECLARE myCursor CURSOR FOR \n    SELECT id, column1 FROM myTable;\n\nOPEN myCursor;\nFETCH NEXT FROM myCursor INTO @id, @column1;\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    UPDATE myTable SET column2 = 'new value' WHERE id = @id;\n    FETCH NEXT FROM myCursor INTO @id, @column1;\nEND\nCLOSE myCursor;\nDEALLOCATE myCursor;\n\n-- Rewrite as a set-based operation\nUPDATE myTable\nSET column2 = 'new value'\nFROM myTable\nINNER JOIN (\n    SELECT id, column1\n    FROM myTable\n) AS subquery ON myTable.id = subquery.id;