DECLARE xmlDoc TEXT;\nSET xmlDoc = '<parent><child>Example 1</child><child>Example 2</child></parent>';\n\nSELECT \n  ExtractValue(xmlDoc, '//child[$i]') AS child_value\nFROM \n  (SELECT @curRow := @curRow + 1 AS row_number\n   FROM   (SELECT @curRow := 0) r\n   CROSS JOIN (SELECT 1 AS n UNION ALL SELECT 2 UNION ALL SELECT 3 UNION ALL SELECT 4) t\n  ) n\nWHERE \n  n.row_number <= ExtractValue(xmlDoc, 'count(//child)');