SELECT \n  e.event_id,\n  ISNULL((SELECT ' + ' + s.name \n           FROM celcat200809.dbo.CT_EVENT_STAFF es \n           INNER JOIN celcat200809.dbo.CT_STAFF s \n             ON es.staff_id = s.staff_id \n           WHERE es.event_id = e.event_id \n           FOR XML PATH('')), '') AS Tutors\nFROM \n  celcat200809.dbo.CT_EVENTS e