-- Original script\nCREATE JOB [MyJob]\n    ON SERVER [myserver]\n    WITH\n    {\n        'DATABASE' = N'mydatabase',\n        'OWNER' = N'myowner'\n    }\n    AS\n    BEGIN\n        -- Job steps here\n    END\nGO\n\n-- Modified script using variables\nDECLARE @databaseName nvarchar(128) = '$(databaseName)'\nDECLARE @ownerName nvarchar(128) = '$(ownerName)'\n\nCREATE JOB [MyJob]\n    ON SERVER [myserver]\n    WITH\n    {\n        'DATABASE' = @databaseName,\n        'OWNER' = @ownerName\n    }\n    AS\n    BEGIN\n        -- Job steps here\n    END\nGO