CREATE PROCEDURE UpdateCustomerDiskUsage\nAS\nBEGIN\n    UPDATE c\n    SET y = (totalDiscUsage - (numberOfUsers * 200)) / (10 * 5)\n    FROM (\n        SELECT CustomerID, \n               SUM(DiskUsageMB) AS totalDiscUsage, \n               COUNT(DISTINCT UserID) AS numberOfUsers\n        FROM StorageInformation\n        GROUP BY CustomerID\n    ) AS subquery\n    INNER JOIN Customers c ON c.CustomerID = subquery.CustomerID;\nEND;