-- Create a new job\nEXEC sp_add_job @job_name = 'Truncate Log File'\n\n-- Add a step to the job\nEXEC sp_add_jobstep \n    @job_name = 'Truncate Log File', \n    @step_name = 'Truncate Log File', \n    @subsystem = 'TSQL', \n    @command = 'BACKUP LOG [database_name] WITH TRUNCATE_ONLY', \n    @retry_attempts = 0, \n    @retry_interval = 0\n\n-- Add a schedule to the job\nEXEC sp_add_jobschedule \n    @job_name = 'Truncate Log File', \n    @name = 'Daily Truncation', \n    @freq_type = 4,  -- daily\n    @freq_interval = 1, \n    @freq_recurrence_factor = 1, \n    @active_start_time = 000000, \n    @active_end_time = 235959