CREATE TABLE Products (\n    IdProduct INT PRIMARY KEY,\n    IdService INT,\n    ProductName VARCHAR(50)\n);\n\nCREATE TABLE Services (\n    IdService INT PRIMARY KEY,\n    ServiceName VARCHAR(50)\n);\n\nINSERT INTO Services (IdService, ServiceName)\nVALUES (1, 'Service 1');\n\nINSERT INTO Products (IdProduct, IdService, ProductName)\nVALUES (1, 1, 'Product 1');\n\nSELECT *\nFROM Products\nINNER JOIN Services USING (IdService);