-- Create a button to open the file\nCREATE BUTTON open_file\nON-MOUSE-CLICK\nBEGIN\n    HOST('cmd /c start "" "' || file_name || '"');\nEND;\n\n-- Create a button to delete the file\nCREATE BUTTON delete_file\nON-MOUSE-CLICK\nBEGIN\n    FileManager.deleteFile(file_name);\n    DELETE FROM files WHERE file_name = file_name;\nEND;\n\n-- Create a button to rename the file\nCREATE BUTTON rename_file\nON-MOUSE-CLICK\nBEGIN\n    FileManager.renameFile(file_name, 'new_name.pdf');\n    UPDATE files SET file_name = 'new_name.pdf' WHERE file_name = file_name;\nEND;