CREATE TABLE actors (\n  id SERIAL PRIMARY KEY,\n  type VARCHAR(50) NOT NULL CHECK (type IN ('worker', 'employer', 'contact')),\n  -- common columns for all actors\n  name VARCHAR(255),\n  email VARCHAR(255)\n);\n\nCREATE TABLE communications (\n  id SERIAL PRIMARY KEY,\n  -- foreign key referencing the actors table\n  actor_id INTEGER NOT NULL REFERENCES actors(id),\n  -- communication-specific columns\n  message TEXT\n);\n\n-- Add a check constraint to ensure data consistency\nALTER TABLE communications\nADD CONSTRAINT valid_actor_type CHECK (\n  EXISTS (\n    SELECT 1\n    FROM actors\n    WHERE actors.id = communications.actor_id\n    AND actors.type = 'worker'\n  )\n);