DECLARE\n  v_height_breakdown METRIC_IMPERIAL_DISTANCE;\n  v_t_id NUMBER;\nBEGIN\n  FOR rec IN (\n    SELECT t_id, get_distance_breakdown(h.height, h.unit_of_measure_id) height_breakdown\n    FROM   heights h\n  ) LOOP\n    v_t_id := rec.t_id;\n    v_height_breakdown := rec.height_breakdown;\n    DBMS_OUTPUT.PUT_LINE(v_t_id || ', ' || v_height_breakdown.meters_feet);\n  END LOOP;\nEND;