SELECT TOP 6 \n  COUPONS.DocType, \n  COUPONS.PayTo, \n  COUPONS.ContactName, \n  COUPONS.ContactNumber,\n  COUPONS.DocFooter, \n  COUPONS.PQBName, \n  COUPONS.LetterDate, \n  COUPONS.RetireeFirstName, \n  COUPONS.RetireeLastName, \n  COUPONS.Address1, \n  COUPONS.Address2, \n  COUPONS.City, \n  COUPONS.State, \n  COUPONS.ZIP, \n  COUPONS.PQBSSN, \n  COUPONS.EmployerCode, \n  COUPONS.AmountDue, \n  COUPONS.DateDue\nFROM \n  COUPONS\nWHERE \n  COUPONS.DateDue >= #01/01/2009# AND COUPONS.DateDue <= #12/01/2009#\nORDER BY \n  COUPONS.DocType, \n  COUPONS.PayTo, \n  COUPONS.ContactName, \n  COUPONS.ContactNumber, \n  COUPONS.DocFooter, \n  COUPONS.PQBName, \n  COUPONS.LetterDate, \n  COUPONS.DateDue