-- On the remote database, create a procedure that returns SYS.ANYDATA\nCREATE OR REPLACE PROCEDURE remote_procedure(p_out OUT SYS.ANYDATA)\nAS\n  l_data RemoteType;\nBEGIN\n  -- populate l_data\n  p_out := SYS.ANYDATA.ConvertObject(l_data);\nEND;\n\n-- On the local database, create a procedure that receives SYS.ANYDATA\nCREATE OR REPLACE PROCEDURE local_procedure(p_in SYS.ANYDATA)\nAS\n  l_data LocalType;\nBEGIN\n  l_data := SYS.ANYDATA.GetObject(p_in);\n  -- process l_data\nEND;\n\n-- Call the remote procedure and pass the result to the local procedure\nDECLARE\n  l_anydata SYS.ANYDATA;\nBEGIN\n  remote_schema.remote_procedure@dblink(l_anydata);\n  local_procedure(l_anydata);\nEND;