DECLARE @TableName nvarchar(128)\nDECLARE @UpdateDate datetime\n\nSET @TableName = 'YourTableName'\nSET @UpdateDate = '2009-05-20'\n\nSELECT *\nFROM (\n  SELECT \n    PrimaryKeyValue, \n    FieldName, \n    NewValue\n  FROM \n    ERAUser.Audit\n  WHERE \n    TableName = @TableName\n    AND UpdateDate = @UpdateDate\n) AS SourceTable\nPIVOT (\n  MAX(NewValue)\n  FOR FieldName IN (\n    SELECT DISTINCT FieldName\n    FROM ERAUser.Audit\n    WHERE TableName = @TableName\n  )\n) AS PivotTable;