SELECT DISTINCT\n  ttg.JobID,\n  ttg.PartCode,\n  ttg.TagHeight,\n  ttg.TagWidth,\n  ttgC.TagHeight AS TagHeightMismatch,\n  ttgC.TagWidth AS TagWidthMismatch\nFROM\n  tblTagGlass ttg\n  INNER JOIN tblTagGlass ttgC ON ttg.JobID = ttgC.JobID AND ttg.PartCode = ttgC.PartCode\nWHERE\n  (ttg.TagHeight != ttgC.TagHeight OR ttg.TagWidth != ttgC.TagWidth)\n  AND ttg.UnitCode < ttgC.UnitCode\nORDER BY\n  ttg.PartCode;