-- Create a proxy account\nEXEC sp_add_proxy @proxy_name = 'MyProxyAccount', @credential_name = 'MyCredential';\n\n-- Grant permissions to the proxy account\nEXEC sp_grant_proxy_to_subsystem @proxy_name = 'MyProxyAccount', @subsystem_name = 'CmdExec';\n\n-- Update the job to run under the proxy account\nEXEC sp_update_jobstep @job_name = 'MyJob', @step_name = 'MyStep', @proxy_name = 'MyProxyAccount';